    public static boolean isPalindromeBruteForce(String s) {
        if (s == null || s.isEmpty() || s.length() == 1) return true;

        s = s.toLowerCase().replaceAll("[^a-z0-9]", "");

        for (int i = 0, j = s.length() - 1; i < j; i++, j--) {
            if (s.charAt(i) != s.charAt(j))
                return false;
        }

        return true;
    }

    // Elapsed time:	000:00:00:00:011:753400
    public static boolean isPalindrome(String s) {
        if (s == null || s.length() <= 1) return true;

        char[] str = s.toCharArray();

        int i = 0;
        int j = str.length - 1;

        while (i < j) {
            if (!isAlphanumeric(str[i])) {
                i++;
                continue;
            }
            if (!isAlphanumeric(str[j])) {
                j--;
                continue;
            }

            if (isDigit(str[i]) && isDigit(str[j])) {
                if (str[i] != str[j]) {
                    return false;
                }
            } else if (isLetter(str[i]) && isLetter(str[j])) {
                // Compare case-insensitively
                if (toLower(str[i]) != toLower(str[j])) {
                    return false;
                }
            } else {
                // One is digit, one is letter
                return false;
            }

            i++;
            j--;
        }

        return true;
    }

    // Helper methods for clarity and performance
    private static boolean isAlphanumeric(char c) {
        return isLetter(c) || isDigit(c);
    }

    private static boolean isLetter(char c) {
        return (c >= 'a' && c <= 'z') || (c >= 'A' && c <= 'Z');
    }

    private static boolean isDigit(char c) {
        return (c >= '0' && c <= '9');
    }

    private static char toLower(char c) {
        if (c >= 'A' && c <= 'Z') {
            return (char) (c + ('a' - 'A'));
        }
        return c;
    }
